select
id, name, sn, product_key, device_name, online_status,model_id as modelId
from t_electricity_cabinet
where del_flag=0

    and tec.name like concat('%',#{name, jdbcType=VARCHAR},'%')


    and tec.tenant_id = #{tenantId}


    and tec.id in

        #{id}



    and modelId = #{modelId}


    and tec.sn like concat('%',#{sn, jdbcType=VARCHAR},'%')

order by create_time desc
limit #{offset}, #{size}

select id, name from t_electricity_cabinet_model where del_flag=0
