mapper.select:
select    id, name, num, voltage
from t_electricity_cabinet_model
where del_flag=0 and num= #{num} and tenant_id=#{tenantId}

select
  id, name, num, voltage, del_flag, create_time, update_time,tenant_id,manufacturer_name,exchange_type,cabinet_size,cell_size,screen_size,waterproof_grade
from t_electricity_cabinet_model
where del_flag=0

    and name = #{query.name}


    and manufacturer_name like concat('%', #{query.manufacturerName}, '%')


    and tenant_id = #{query.tenantId}

order by update_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_electricity_cabinet_model
where del_flag=0

    and name = #{query.name}


    and tenant_id = #{query.tenantId}
