mapper.select:
select
teco.id as id, teco.order_id as orderId, teco.phone as phone, teco.uid as uid, teco.pay_amount as payAmount,
teco.electricity_cabinet_id as electricityCabinetId, teco.old_electricity_battery_sn as
oldElectricityBatterySn,
teco.new_electricity_battery_sn as newElectricityBatterySn, teco.old_cell_no as oldCellNo, teco.new_cell_no
as newCellNo, teco.status as status, teco.payment_method as paymentMethod, teco.source as source, teco.remark
as remark,
teco.switch_begin_time as switchBeginTime, teco.switch_end_time as switchEndTime, teco.create_time
as createTime, teco.update_time as updateTime
from electricity.t_electricity_cabinet_order as teco


        and teco.order_id = #{query.orderId}


        and teco.phone = #{query.phone}


        and teco.status = #{query.status}


        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.payment_method = #{query.paymentMethod}


        and teco.uid = #{query.uid}


order by teco.update_time desc

select count(1)
from electricity.t_electricity_cabinet_order


        and create_time >=#{first}


        and create_time <=#{now}


        and electricity_cabinet_id in

            #{id}

select count(1)
from electricity.t_electricity_cabinet_order
where status=6

        and create_time >=#{first}


        and create_time <=#{now}


        and electricity_cabinet_id in

            #{id}

mapper.update:
update electricity.t_electricity_cabinet_order
set status = 8 , update_time = #{updateTime}
where  order_id = #{orderId} and status in(1,2,3);
