mapper.select:
select
teco.id, teco.order_id, teco.phone, teco.uid, teco.pay_amount, teco.electricity_cabinet_id,
teco.old_electricity_battery_sn,
teco.new_electricity_battery_sn, teco.old_cell_no, teco.new_cell_no, teco.status, teco.payment_method,
teco.source, teco.remark, teco.switch_begin_time,
teco.switch_end_time, teco.create_time, teco.update_time, teco.franchisee_id,teco.channel
from t_electricity_cabinet_order teco


        and teco.order_id = #{query.orderId}


        and teco.phone = #{query.phone}


        and teco.name = #{query.electricityCabinetName}


        and teco.old_cell_no = #{query.oldCellNo}


        and teco.status = #{query.status}


        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.payment_method = #{query.paymentMethod}


        and teco.source = #{query.source}


        and teco.uid = #{query.uid}


        and teco.electricity_cabinet_id = #{query.eid}


        and teco.franchisee_id in

            #{id}



        and teco.store_id in

            #{id}



        and teco.tenant_id = #{query.tenantId}


        and (teco.old_electricity_battery_sn = #{query.batterySn} or teco.new_electricity_battery_sn = #{query.batterySn})


        and teco.old_electricity_battery_sn = #{query.oldElectricityBatterySn}


        and teco.new_electricity_battery_sn = #{query.newElectricityBatterySn}


        and teco.franchisee_id = #{query.franchiseeId}


        and teco.channel = #{query.channel}


order by teco.create_time desc
limit #{query.offset}, #{query.size}

select count(1)
from t_electricity_cabinet_order teco


        and teco.order_id =#{query.orderId}


        and teco.phone = #{query.phone}


        and teco.name = #{query.electricityCabinetName}


        and teco.old_cell_no = #{query.oldCellNo}


        and teco.status = #{query.status}


        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.payment_method = #{query.paymentMethod}


        and teco.source = #{query.source}


        and teco.uid = #{query.uid}


        and teco.electricity_cabinet_id = #{query.eid}


        and teco.tenant_id = #{query.tenantId}


        and teco.franchisee_id = #{query.franchiseeId}


        and (teco.old_electricity_battery_sn = #{query.batterySn} or teco.new_electricity_battery_sn = #{query.batterySn})


        and teco.old_electricity_battery_sn = #{query.oldElectricityBatterySn}


        and teco.new_electricity_battery_sn = #{query.newElectricityBatterySn}


        and teco.franchisee_id in

            #{id}



        and teco.store_id in

            #{id}



        and teco.channel = #{query.channel}

select count(1)
from t_electricity_cabinet_order teco left join t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id

    tec.del_flag=0

        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.tenant_id = #{query.tenantId}


        and teco.electricity_cabinet_id = #{query.electricityCabinetId}


        and teco.electricity_cabinet_id in

            #{id}

select count(1) as exchangeFrequency, teco.electricity_cabinet_id as eleId,teco.store_id
from t_electricity_cabinet_order teco left join t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id

    tec.del_flag=0

        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.electricity_cabinet_id = #{query.electricityCabinetId}


        and teco.tenant_id = #{query.tenantId}


        and teco.electricity_cabinet_id in

            #{id}



group by teco.electricity_cabinet_id ,teco.store_id
limit #{query.offset}, #{query.size}

select count(1) as exchangeFrequency, teco.electricity_cabinet_id as eleId,teco.store_id
from t_electricity_cabinet_order teco left join t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id

    tec.del_flag=0

        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.electricity_cabinet_id = #{query.electricityCabinetId}


        and teco.tenant_id = #{query.tenantId}


        and teco.electricity_cabinet_id in

            #{id}



group by teco.electricity_cabinet_id ,teco.store_id

select count(1)
from t_electricity_cabinet_order


        and create_time >=#{first}


        and create_time <=#{now}


        and electricity_cabinet_id in

            #{id}



        and tenant_id = #{tenantId}

select count(1)
from t_electricity_cabinet_order
where order_seq=7.0

    and create_time >=#{first}


    and create_time <=#{now}


    and electricity_cabinet_id in

        #{id}



    and tenant_id = #{tenantId}

select id, uid, new_electricity_battery_sn
from t_electricity_cabinet_order
where uid = #{uid}
  and tenant_id = #{tenantId}
order by id desc limit 1

select
id, uid
from t_electricity_cabinet_order


        and electricity_cabinet_id = #{eid}


        and create_time >=#{todayStartTimeStamp}


        and create_time <=#{todayEndTimeStamp}


        and tenant_id = #{tenantId}

SELECT
id, uid
FROM t_electricity_cabinet_order


        and electricity_cabinet_id = #{eid}


        and create_time >=#{startTimeStamp}


        and create_time <= #{endTimeStamp}


        and tenant_id = #{tenantId}

select
from t_electricity_cabinet_order teco
order by teco.create_time desc limit #{query.offset}, #{query.size}

select 1
from t_electricity_cabinet_order
where electricity_cabinet_id = #{electricityCabinetId}
  and id > #{id}
  and old_cell_no = #{oldCellNo}
order by id limit 1

select order_id,uid from  t_electricity_cabinet_order
where order_id in

    #{orderId}
