mapper.select:
select
  id, order_id, phone, uid, pay_amount, electricity_cabinet_id, old_cell_no, new_cell_no, status, payment_method, source, remark, switch_beginning_time, switch_end_time, create_time, update_time
from electricity.t_electricity_cabinet_order


        and id = #{id}


        and order_id = #{orderId}


        and phone = #{phone}


        and uid = #{uid}


        and pay_amount = #{payAmount}


        and electricity_cabinet_id = #{electricityCabinetId}


        and old_cell_no = #{oldCellNo}


        and new_cell_no = #{newCellNo}


        and status = #{status}


        and payment_method = #{paymentMethod}


        and source = #{source}


        and remark = #{remark}


        and switch_beginning_time = #{switchBeginningTime}


        and switch_end_time = #{switchEndTime}


        and create_time = #{createTime}


        and update_time = #{updateTime}
