mapper.select:
select
teco.id as id, teco.order_id as order_id, teco.phone as phone, teco.uid as uid, teco.pay_amount as pay_amount,
teco.electricity_cabinet_id as electricity_cabinet_id, teco.old_electricity_battery_sn as
old_electricity_battery_sn,
teco.new_electricity_battery_sn as new_electricity_battery_sn, teco.old_cell_no as old_cell_no, teco.new_cell_no
as new_cell_no, teco.status as status, teco.payment_method as payment_method, teco.source as source, teco.remark
as remark,
teco.switch_begin_time as switch_begin_time, teco.switch_end_time as switch_end_time, teco.create_time
as create_time, teco.update_time as update_time,tec.area_id as area_id
from electricity.t_electricity_cabinet_order as teco
left join electricity.t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id


        and teco.order_id = #{query.orderId}


        and teco.phone = #{query.phone}


        and teco.status = #{query.status}


        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and tec.area_id = #{query.areaId}


order by teco.update_time desc
limit #{query.offset}, #{query.size}

select
id, order_id, phone, uid, pay_amount, electricity_cabinet_id, old_electricity_battery_sn,
new_electricity_battery_sn, old_cell_no, new_cell_no, status, payment_method, source, remark,
switch_begin_time, switch_end_time, create_time, update_time
from electricity.t_electricity_cabinet_order


        and id = #{id}


        and order_id = #{orderId}


        and phone = #{phone}


        and uid = #{uid}


        and pay_amount = #{payAmount}


        and electricity_cabinet_id = #{electricityCabinetId}


        and old_electricity_battery_sn = #{oldElectricityBatterySn}


        and new_electricity_battery_sn = #{newElectricityBatterySn}


        and old_cell_no = #{oldCellNo}


        and new_cell_no = #{newCellNo}


        and status = #{status}


        and payment_method = #{paymentMethod}


        and source = #{source}


        and remark = #{remark}


        and switch_begin_time = #{switchBeginTime}


        and switch_end_time = #{switchEndTime}


        and create_time = #{createTime}


        and update_time = #{updateTime}

mapper.update:
update electricity.t_electricity_cabinet_order
set status = 8 , update_time = #{updateTime}
where  order_id = #{orderId} and status in(1,2,3);
