mapper.select:
select
teco.id, teco.order_id, teco.phone, teco.uid, teco.pay_amount, teco.electricity_cabinet_id,
teco.old_electricity_battery_sn,
teco.new_electricity_battery_sn, teco.old_cell_no, teco.new_cell_no, teco.status, teco.payment_method,
teco.source, teco.remark, teco.switch_begin_time,
teco.switch_end_time, teco.create_time, teco.update_time
from t_electricity_cabinet_order teco left join t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id


        and teco.order_id like concat('%',#{query.orderId,jdbcType=VARCHAR},'%')


        and teco.phone = #{query.phone}


        and tec.name = #{query.electricityCabinetName}


        and teco.old_cell_no = #{query.oldCellNo}


        and teco.status = #{query.status}


        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.payment_method = #{query.paymentMethod}


        and teco.source = #{query.source}


        and teco.uid = #{query.uid}


        and teco.electricity_cabinet_id in

            #{id}



        and teco.tenant_id = #{query.tenantId}


order by teco.update_time desc
limit #{query.offset}, #{query.size}

select count(1)
from t_electricity_cabinet_order


        and order_id like concat('%',#{query.orderId,jdbcType=VARCHAR},'%')


        and phone = #{query.phone}


        and status = #{query.status}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


        and payment_method = #{query.paymentMethod}


        and source = #{query.source}


        and uid = #{query.uid}


        and electricity_cabinet_id in

            #{id}



        and tenant_id = #{query.tenantId}

select count(1)
from t_electricity_cabinet_order teco left join t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id

    tec.del_flag=0

        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.tenant_id = #{query.tenantId}


        and teco.electricity_cabinet_id = #{query.electricityCabinetId}


        and teco.electricity_cabinet_id in

            #{id}

select count(1) as exchangeFrequency, teco.electricity_cabinet_id as eleId,teco.store_id
from t_electricity_cabinet_order teco left join t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id

    tec.del_flag=0

        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.electricity_cabinet_id = #{query.electricityCabinetId}


        and teco.tenant_id = #{query.tenantId}


        and teco.electricity_cabinet_id in

            #{id}



    group by teco.electricity_cabinet_id ,teco.store_id
limit #{query.offset}, #{query.size}

select count(1) as exchangeFrequency, teco.electricity_cabinet_id as eleId,teco.store_id
from t_electricity_cabinet_order teco left join t_electricity_cabinet tec on teco.electricity_cabinet_id=tec.id

    tec.del_flag=0

        and teco.create_time >=#{query.beginTime}


        and teco.create_time <=#{query.endTime}


        and teco.electricity_cabinet_id = #{query.electricityCabinetId}


        and teco.tenant_id = #{query.tenantId}


        and teco.electricity_cabinet_id in

            #{id}



group by teco.electricity_cabinet_id ,teco.store_id

select count(1)
from t_electricity_cabinet_order


        and create_time >=#{first}


        and create_time <=#{now}


        and electricity_cabinet_id in

            #{id}



        and tenant_id = #{tenantId}

select count(1)
from t_electricity_cabinet_order
where order_seq=7.0

    and create_time >=#{first}


    and create_time <=#{now}


    and electricity_cabinet_id in

        #{id}



    and tenant_id = #{tenantId}

select from_unixtime(create_time / 1000, '%Y-%m-%d') date, count(*) as count
from t_electricity_cabinet_order where create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}

    and electricity_cabinet_id in

        #{id}



    and tenant_id = #{tenantId}

group by date
order by date desc
