mapper.select:
select
  id, o_id, create_time, electricity_cabinet_id, cell_no, status,type, uid
from electricity.t_electricity_cabinet_order_oper_history


        and id = #{id}


        and o_id = #{oId}


        and create_time = #{createTime}


        and electricity_cabinet_id = #{electricityCabinetId}


        and cell_no = #{cellNo}


        and status = #{status}


        and type = #{type}


        and uid = #{uid}
