mapper.select:
select
id, electricity_cabinet_id, cell_no, status, msg, uid, user_name, operate_type, create_time, command
from t_electricity_cabinet_physics_oper_record


        and id = #{id}


        and electricity_cabinet_id = #{electricityCabinetId}


        and cell_no = #{cellNo}


        and status = #{status}


        and msg = #{msg}


        and uid = #{uid}


        and user_name = #{userName}


        and operate_type = #{operateType}


        and create_time = #{createTime}


        and command = #{command}

select
    ecpor.id, ecpor.status, ecpor.msg, ecpor.uid, ecpor.user_name, ecpor.operate_type, ecpor.create_time, ui.phone
from t_electricity_cabinet_physics_oper_record ecpor
left join t_user ui on ecpor.uid = ui.uid


        and ecpor.electricity_cabinet_id = #{eleId}


        and ecpor.operate_type = #{operateType}


        and ecpor.create_time between #{beginTime} and #{endTime}


        and ecpor.cell_no = #{cellNo}


        and ecpor.user_name like concat('%', #{userName}, '%')


        and ui.phone like concat('%', #{phone}, '%')


order by create_time desc

    limit #{offset}, #{size}

select
    count( * )
from t_electricity_cabinet_physics_oper_record ecpor
left join t_user ui on ecpor.uid = ui.uid


        and ecpor.electricity_cabinet_id = #{eleId}


        and ecpor.operate_type = #{operateType}


        and ecpor.create_time between #{beginTime} and #{endTime}


        and ecpor.cell_no = #{cellNo}


        and ecpor.user_name like concat('%', #{userName}, '%')


        and ui.phone like concat('%', #{phone}, '%')

select 1
from t_electricity_cabinet_physics_oper_record
where electricity_cabinet_id = #{electricityCabinetId}
  and cell_no = #{oldCellNo}
  and create_time > #{createTime} limit 1
