mapper.select:
select
id, electricity_cabinet_id, product_key, device_name, tenant_id, server_begin_time,
server_end_time, del_flag, create_time, update_time
from t_electricity_cabinet_server


        and id = #{id}


        and electricity_cabinet_id = #{electricityCabinetId}


        and product_key = #{productKey}


        and device_name = #{deviceName}


        and tenant_id = #{tenantId}


        and server_begin_time = #{serverBeginTime}


        and server_end_time = #{serverEndTime}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}

select ecs.id, ecs.electricity_cabinet_id, ecs.product_key, ecs.device_name, ecs.tenant_id, ecs.server_begin_time, ecs.server_end_time, ecs.del_flag, ecs.create_time, ecs.update_time from t_electricity_cabinet_server ecs left join t_electricity_cabinet ec on ecs.electricity_cabinet_id = ec.id left join t_tenant t on ecs.tenant_id = t.id
ecs.del_flag = 0
and ec.name like concat('%', #{eleName}, '%')
and ec.device_name like concat('%', #{deviceName}, '%')
and t.name like concat('%', #{tenantName}, '%')
and ecs.server_begin_time > #{serverTimeStart}
and ecs.server_end_time < #{serverTimeEnd}
order by server_end_time limit #{offset}, #{size}
