mapper.select:
select
id, ele_server_id, create_uid, old_server_begin_time, old_server_end_time,
new_server_begin_time, new_server_end_time, create_time
from t_electricity_cabinet_server_oper_record


        and id = #{id}


        and ele_server_id = #{eleServerId}


        and create_uid = #{createUid}


        and old_server_begin_time = #{oldServerBeginTime}


        and old_server_end_time = #{oldServerEndTime}


        and new_server_begin_time = #{newServerBeginTime}


        and new_server_end_time = #{newServerEndTime}


        and create_time = #{createTime}

select ecsor.id, ecsor.ele_server_id, ecsor.create_uid, ecsor.old_server_begin_time, ecsor.old_server_end_time, ecsor.new_server_begin_time, ecsor.new_server_end_time, ecsor.create_time, u.name as user_name, ec.name as ele_name from t_electricity_cabinet_server_oper_record ecsor left join t_user u on u.uid = ecsor.create_uid left join t_electricity_cabinet_server ecs on ecs.id = ecsor.ele_server_id left join t_electricity_cabinet ec on ecs.electricity_cabinet_id = ec.id where ecsor.ele_server_id = #{eleServerId}
and u.name like concat('%', #{createUserName}, '%')
and ecsor.tenant_id = #{tenantId}
order by ecsor.create_time desc limit #{offset}, #{size}

select
count(*)
from t_electricity_cabinet_server_oper_record ecsor
left join t_user u on u.uid = ecsor.create_uid
where
ecsor.ele_server_id = #{eleServerId}

    and u.name = #{createUserName}


    and ecsor.tenant_id = #{tenantId}
