mapper.select:
select
tec.id,tec.sn,
tec.model,tec.longitude,tec.latitude,tec.status,tec.uid,tec.user_name,tec.store_id, tec.phone
,tec.create_time,tec.update_time,ts.name as storeName,tec.uid
from t_electricity_car tec left join t_store ts on tec.store_id=ts.id left join t_franchisee_user_info tfui on
tec.user_info_id=tfui.user_info_id
where tec.del_flag=0

    and tec.sn = #{query.sn}


    and tec.model = #{query.model}


    and tec.status = #{query.status}


    and tec.phone = #{query.phone}


    and tec.store_id in

        #{id}



    and ts.franchisee_id in

        #{id}



    and tfui.now_electricity_battery_sn = #{query.batterySn}


    and tec.create_time >=#{query.beginTime}


    and tec.create_time <=#{query.endTime}


    and tec.tenant_id = #{query.tenantId}

order by tec.create_time desc
limit #{query.offset}, #{query.size}

select
count(1)
from t_electricity_car tec left join t_store ts on tec.store_id=ts.id
left join t_franchisee_user_info tfui on
tec.user_info_id=tfui.user_info_id
where tec.del_flag=0

    and tec.sn = #{query.sn}


    and tec.model = #{query.model}


    and tec.status = #{query.status}


    and tec.store_id = #{query.storeId}


    and ts.franchisee_id in

        #{id}



    and tec.battery_sn = #{query.batterySn}


    and tec.create_time >=#{query.beginTime}


    and tec.create_time <=#{query.endTime}


    and tec.tenant_id = #{query.tenantId}

order by tec.create_time desc

select count(1)
from t_electricity_car

    del_flag=0

        and tenant_id = #{tenantId}


        and store_id in

            #{id}
