mapper.select:


    `del_flag` = 0 AND `store_id` IN

        #{storeId}


ORDER BY `rented_quantity` DESC
LIMIT #{size}

select
tecm.id, tecm.name,tecm.franchisee_id, tecm.del_flag, tecm.create_time, tecm.update_time,tecm.tenant_id,
tecm.store_id, tecm.rent_type, tecm.car_deposit, tecm.other_properties, tecm.`rented_quantity`, tecm.manufacturer_name
from t_electricity_car_model tecm
where tecm.del_flag=0

    and tecm.name = #{query.name}


    and manufacturer_name like concat('%', #{query.manufacturerName}, '%')


    and tecm.tenant_id = #{query.tenantId}


    and tecm.store_id = #{query.storeId}


    and tecm.franchisee_id = #{query.franchiseeId}


    and tecm.franchisee_id in

        #{id}



    and tecm.store_id in

        #{id}


order by tecm.update_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_electricity_car_model tecm
where tecm.del_flag=0

    and tecm.name = #{query.name}


    and manufacturer_name like concat('%', #{query.manufacturerName}, '%')


    and tecm.tenant_id = #{query.tenantId}


    and tecm.franchisee_id = #{query.franchiseeId}


    and tecm.franchisee_id in

        #{id}



    and tecm.store_id in

        #{id}



    and tecm.store_id = #{query.storeId}




        and id = #{id}


        and name = #{name}


        and franchisee_id = #{franchiseeId}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}


        and store_id = #{storeId}


        and rent_type = #{rentType}


        and car_deposit = #{carDeposit}


        and other_properties = #{otherProperties}


        AND `rented_quantity` = #{rentedQuantity}


order by create_time desc
limit #{offset}, #{size}

select id, name, rent_type, car_deposit from t_electricity_car_model

    franchisee_id = #{franchiseeId}
    and tenant_id = #{tenantId}
    and del_flag = 0

        and name like concat('%', #{name}, '%')

select id from t_electricity_car_model
where del_flag = 0 and store_id in

    #{id}

select id,name from t_electricity_car_model


        and name like concat('%',#{name,jdbcType=VARCHAR},'%')


        and franchisee_id = #{franchiseeId}


        and del_flag = #{delFlag}


        and tenant_id = #{tenantId}


        and store_id = #{storeId}


        AND `rented_quantity` = #{rentedQuantity}
