mapper.select:
select
tecm.id, tecm.name,tecm.franchisee_id, tecm.del_flag, tecm.create_time, tecm.update_time,tecm.tenant_id
from t_electricity_car_model tecm left join t_franchisee tf on tecm.franchisee_id=tf.id
left join t_store ts on ts.franchisee_id=tecm.franchisee_id
where tecm.del_flag=0

    and tecm.name = #{query.name}


    and tecm.tenant_id = #{query.tenantId}


    and tecm.franchisee_id = #{query.franchiseeId}


    and ts.store_id = #{query.storeId}

order by tecm.update_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_electricity_car_model tecm  left join t_store ts on ts.franchisee_id=tecm.franchisee_id
where del_flag=0

    and tecm.name = #{query.name}


    and tecm.tenant_id = #{query.tenantId}


    and tecm.franchisee_id = #{query.franchiseeId}


    and ts.store_id = #{query.storeId}
