mapper.select:
SELECT id,name,valid_days,model_type,battery_Type,status
FROM t_electricity_member_card
where del_flag=0

    and name like concat('%',#{name,jdbcType=VARCHAR},'%')


    AND status = #{status}


    and card_model = #{cardModel}


    and tenant_id = #{tenantId}


    and franchisee_id = #{franchiseeId}

ORDER BY id DESC

SELECT
temc.id,temc.name,temc.type,temc.holiday_price,temc.price,temc.valid_days,temc.max_use_count,temc.status,temc.create_time,temc.update_time,
temc.limit_count,temc.franchisee_id,temc.del_flag,temc.tenant_id,temc.model_type,temc.battery_Type,temc.is_bind_activity,temc.activity_id,
temc.card_model,tecm.name as carModel,temc.car_model_id,tf.name as franchiseeName
FROM t_electricity_member_card temc left join t_electricity_car_model tecm on temc.car_model_id=tecm.id
left join t_franchisee tf on temc.franchisee_id=tf.id
where temc.del_flag=0

    AND temc.status = #{status}


    AND temc.type = #{type}


    and temc.tenant_id = #{tenantId}


    and temc.franchisee_id in

        #{id}


ORDER BY temc.create_time DESC
limit #{offset}, #{size}

SELECT *
FROM t_electricity_member_card
where status=0 and franchisee_id=#{id} and del_flag=0

    and battery_type = #{batteryType}


    and card_model = #{cardModel}

ORDER BY create_time DESC
limit #{offset}, #{size}

SELECT COUNT(1)
FROM t_electricity_member_card temc left join t_electricity_car_model tecm on temc.car_model_id=tecm.id
where temc.del_flag=0

    AND temc.status = #{status}


    AND temc.type = #{type}


    and temc.tenant_id = #{tenantId}


    and temc.franchisee_id in

        #{id}



    and temc.name = #{name}

SELECT *
FROM t_electricity_member_card
