mapper.select:
SELECT
temc.id,temc.name,temc.type,temc.holiday_price,temc.price,temc.valid_days,temc.max_use_count,temc.status,temc.create_time,temc.update_time,
temc.limit_count,temc.franchisee_id,temc.del_flag,temc.tenant_id,temc.model_type,temc.battery_Type,temc.is_bind_activity,temc.activity_id,temc.max_use_distance,
temc.card_model,tecm.name as carModel,temc.car_model_id
FROM t_electricity_member_card temc left join t_electricity_car_model tecm on temc.car_model_id=tecm.id
where temc.del_flag=0

    AND temc.status = #{status}


    AND temc.type = #{type}


    and temc.tenant_id = #{tenantId}


    and temc.franchisee_id = #{franchiseeId}


    and temc.card_model = #{cardModel}


    and temc.car_model_id = #{cardModelId}

ORDER BY temc.create_time DESC
limit #{offset}, #{size}

SELECT *
FROM t_electricity_member_card
where status=0 and franchisee_id=#{id} and del_flag=0

    and battery_type = #{batteryType}

ORDER BY create_time DESC
limit #{offset}, #{size}

SELECT COUNT(1)
FROM t_electricity_member_card
where del_flag=0

    AND status = #{status}


    AND type = #{type}


    and tenant_id = #{tenantId}

SELECT *
FROM t_electricity_member_card
where del_flag=0

    AND status = #{status}


    AND type = #{type}


    and tenant_id = #{tenantId}


    and franchisee_id = #{franchiseeId}

ORDER BY create_time DESC
limit #{offset}, #{size}

SELECT COUNT(1)
FROM t_electricity_member_card
where del_flag=0

    AND status = #{status}


    AND type = #{type}


    and tenant_id = #{tenantId}


    and franchisee_id = #{franchiseeId}
