mapper.select:
select
id, uid, franchisee_id, create_time, update_time, total_income, balance, tenant_id, del_flag,withdraw
from t_franchisee_amount

	del_flag = 0

		and franchisee_id = #{franchiseeId}


		and create_time >= #{startTime}


		and create_time <= #{endTime}


		and tenant_id = #{tenantId}

	order by create_time desc
	limit #{offset}, #{size}

select count(1)
from t_franchisee_amount

	del_flag = 0

		and franchisee_id = #{franchiseeId}


		and create_time >= #{startTime}


		and create_time <= #{endTime}


		and tenant_id = #{tenantId}

mapper.update:
update t_franchisee_amount
set del_flag = 1


   and franchisee_id = #{id}
