mapper.select:
select   id, name  from t_franchisee
where del_flag=0

    and name like concat('%',#{name,jdbcType=VARCHAR},'%')


    and tenant_id = #{tenantId}

order by id
limit #{offset}, #{size}


where del_flag=0

    and id = #{id}


    and region_id = #{regionId}


    and cid = #{cid}


    and tenant_id = #{tenantId}


    and id in

        #{id}

select
id, cid, name, uid, del_flag,
create_time, update_time, battery_deposit,tenant_id,percent,
model_type,model_battery_deposit,battery_service_fee,is_open_service_fee,disable_card_time_type,region_id
from t_franchisee
where del_flag=0

    and name like concat('%',#{query.name,jdbcType=VARCHAR},'%')


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and tenant_id = #{query.tenantId}


    and uid = #{query.uid}


    and id in

        #{id}


order by create_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_franchisee
where del_flag=0

    and name like concat('%',#{query.name,jdbcType=VARCHAR},'%')


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and tenant_id = #{query.tenantId}


    and uid = #{query.uid}


    and id in

        #{id}


order by create_time desc
