mapper.select:
select
id, order_id, uid, name, phone, id_card, operate_uid, operate_name, pay_amount, alipay_amount,
type, create_time, update_time, tenant_id, remark, pay_status
from t_free_deposit_alipay_history


        and id = #{id}


        and order_id = #{orderId}


        and uid = #{uid}


        and name = #{name}


        and phone = #{phone}


        and id_card = #{idCard}


        and operate_uid = #{operateUid}


        and operate_name = #{operateName}


        and pay_amount = #{payAmount}


        and alipay_amount = #{alipayAmount}


        and type = #{type}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}

select fdah.id, fdah.auth_pay_order_id, fdah.order_id, fdah.name as 'realName', fdah.phone, fdah.id_card, fdah.operate_name, fdah.pay_amount, fdah.alipay_amount, fdah.type, fdah.create_time, fdah.tenant_id, fdah.remark, fdah.pay_status, fdah.franchisee_id from t_free_deposit_alipay_history fdah
and fdah.order_id like concat('%', #{orderId}, '%')
and fdah.name like concat('%', #{userName}, '%')
and fdah.phone like concat('%', #{phone}, '%')
and fdah.id_card like concat('%', #{idCard}, '%')
and fdah.type = #{type}
and fdah.uid = #{uid}
and fdah.pay_status = #{payStatus}
and fdah.create_time > #{beginTime}
and fdah.create_time < #{endTime}
and fdah.tenant_id = #{tenantId}
and fdah.franchisee_id = #{franchiseeId}
and fdah.auth_pay_order_id like concat('%', #{authPayOrderId}, '%')
and fdah.franchisee_id in
#{id}
and fdah.store_id in
#{id}
order by fdah.create_time desc limit #{offset}, #{size}

select
count(*)
from t_free_deposit_alipay_history fdah


        and fdah.order_id like concat('%', #{orderId}, '%')


        and fdah.name like concat('%', #{userName}, '%')


        and fdah.phone like concat('%', #{phone}, '%')


        and fdah.id_card like concat('%', #{idCard}, '%')


        and fdah.type = #{type}


        and fdah.uid = #{uid}


        and fdah.pay_status = #{payStatus}


