mapper.select:
select
id, order_id, uid, name, phone, id_card, operate_uid, operate_name, pay_amount, alipay_amount,
type, create_time, update_time, tenant_id
from t_free_deposit_alipay_history


        and id = #{id}


        and order_id = #{orderId}


        and uid = #{uid}


        and name = #{name}


        and phone = #{phone}


        and id_card = #{idCard}


        and operate_uid = #{operateUid}


        and operate_name = #{operateName}


        and pay_amount = #{payAmount}


        and alipay_amount = #{alipayAmount}


        and type = #{type}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}

select
id, order_id, name, phone, id_card, operate_name, pay_amount, alipay_amount,
type, create_time, tenant_id
from t_free_deposit_alipay_history


        and order_id like concat('%', #{orderId}, '%')


        and name like concat('%', #{userName}, '%')


        and phone like concat('%', #{phone}, '%')


        and id_card like concat('%', #{idCard}, '%')


        and type = #{type}


        and create_time > #{beginTime}


        and create_time < #{endTime}


        and tenant_id = #{tenantId}


order by create_time desc
limit #{offset}, #{size}

select
count(*)
from t_free_deposit_alipay_history


        and order_id like concat('%', #{orderId}, '%')


        and name like concat('%', #{userName}, '%')


        and phone like concat('%', #{phone}, '%')


        and id_card like concat('%', #{idCard}, '%')


        and type = #{type}


        and create_time > #{beginTime}


        and create_time < #{endTime}


        and tenant_id = #{tenantId}
