mapper.select:
select

from t_free_deposit_expire_record
where tenant_id = #{tenantId}
and status = #{status}

    and uid = #{uid}


    and deposit_type = #{depositType}


    and franchisee_id = #{franchiseeId}


    and franchisee_id in

        #{franchiseeId}


order by create_time desc
limit #{offset},#{size}

select count(1)
from t_free_deposit_expire_record
where tenant_id = #{tenantId}
and status = #{status}

    and uid = #{uid}


    and deposit_type = #{depositType}


    and franchisee_id = #{franchiseeId}


    and franchisee_id in

        #{franchiseeId}
