mapper.update:
update t_free_deposit_expire_record
set status = 3,update_time = UNIX_TIMESTAMP() * 1000
, operate_time = UNIX_TIMESTAMP() * 1000
, operate_uid = #{operateUid}
where id in

    #{id}

mapper.select:
select

from t_free_deposit_expire_record
where tenant_id = #{tenantId}
and status = #{status}

    and uid = #{uid}


    and deposit_type = #{depositType}


    and franchisee_id = #{franchiseeId}


    and franchisee_id in

        #{franchiseeId}




    order by tui.update_time desc


    order by id desc

limit #{offset},#{size}

select count(1)
from t_free_deposit_expire_record
where tenant_id = #{tenantId}
and status = #{status}

    and uid = #{uid}


    and deposit_type = #{depositType}


    and franchisee_id = #{franchiseeId}


    and franchisee_id in

        #{franchiseeId}
