mapper.select:
select
tfdo.id,
tfdo.uid,
tfdo.order_id,
tfdo.auth_no,
tfdo.phone,
tfdo.id_card,
tfdo.real_name,
tfdo.trans_amt,
tfdo.withheld_amt,
tfdo.pay_trans_amt,
tfdo.auth_status,
tfdo.pay_status,
tfdo.tenant_id,
tfdo.type,
tfdo.deposit_type,
tfdo.create_time,
tfdo.update_time,
tfdo.franchisee_id
from t_free_deposit_order tfdo
where tfdo.tenant_id=#{tenantId}

    and tfdo.auth_status = #{authStatus}


    and tfdo.pay_status = #{payStatus}


    and tfdo.deposit_type = #{depositType}


    and tfdo.order_id = #{orderId}


    and tfdo.phone = #{phone}


    and tfdo.real_name like concat('%',#{realName,jdbcType=VARCHAR},'%')


    and tfdo.uid = #{uid}


    and tfdo.create_time >= #{startTime}


    and tfdo.create_time <= #{endTime}


    and tfdo.franchisee_id = #{franchiseeId}


    and tfdo.franchisee_id in

        #{id}



    and tfdo.store_id in

        #{id}


order by tfdo.id desc
limit #{offset}, #{size}

select
count(1)
from t_free_deposit_order tfdo
where tfdo.tenant_id=#{tenantId}

    and tfdo.auth_status = #{authStatus}


    and tfdo.pay_status = #{payStatus}


    and tfdo.deposit_type = #{depositType}


    and tfdo.order_id = #{orderId}


    and tfdo.phone = #{phone}


    and tfdo.real_name like concat('%',#{realName,jdbcType=VARCHAR},'%')


    and tfdo.uid = #{uid}


    and tfdo.create_time >= #{startTime}


    and tfdo.create_time <= #{endTime}


    and tfdo.franchisee_id = #{franchiseeId}


    and tfdo.franchisee_id in

        #{id}



    and tfdo.store_id in

        #{id}

select
id, uid, order_id, auth_no, phone, id_card, real_name, trans_amt, pay_trans_amt, auth_status, pay_status,
tenant_id, type, deposit_type, create_time, update_time
from t_free_deposit_order


        and id = #{id}


        and uid = #{uid}


        and order_id = #{orderId}


        and auth_no = #{authNo}


        and phone = #{phone}


        and id_card = #{idCard}


        and real_name = #{realName}


        and trans_amt = #{transAmt}


        and pay_trans_amt = #{payTransAmt}


        and auth_status = #{authStatus}


        and pay_status = #{payStatus}


        and tenant_id = #{tenantId}


        and type = #{type}


        and deposit_type = #{depositType}


        and create_time = #{createTime}


        and update_time = #{updateTime}
