mapper.select:
select
id, uid, order_id, auth_no, phone, id_card, real_name, trans_amt, pay_trans_amt, auth_status, pay_status,
tenant_id, type, deposit_type, create_time, update_time
from t_free_deposit_order
where tenant_id=#{tenantId}

    and auth_status = #{authStatus}


    and pay_status = #{payStatus}


    and deposit_type = #{depositType}


    and order_id = #{orderId}


    and phone = #{phone}


    and real_name like concat('%',#{realName,jdbcType=VARCHAR},'%')


    and create_time >= #{startTime}


    and create_time <= #{endTime}

order by create_time desc
limit #{offset}, #{size}

select
count(1)
from t_free_deposit_order
where tenant_id=#{tenantId}

    and auth_status = #{authStatus}


    and pay_status = #{payStatus}


    and deposit_type = #{depositType}


    and order_id = #{orderId}


    and phone = #{phone}


    and real_name like concat('%',#{realName,jdbcType=VARCHAR},'%')


    and create_time >= #{startTime}


    and create_time <= #{endTime}

select
id, uid, order_id, auth_no, phone, id_card, real_name, trans_amt, pay_trans_amt, auth_status, pay_status,
tenant_id, type, deposit_type, create_time, update_time
from t_free_deposit_order


        and id = #{id}


        and uid = #{uid}


        and order_id = #{orderId}


        and auth_no = #{authNo}


        and phone = #{phone}


        and id_card = #{idCard}


        and real_name = #{realName}


        and trans_amt = #{transAmt}


        and pay_trans_amt = #{payTransAmt}


        and auth_status = #{authStatus}


        and pay_status = #{payStatus}


        and tenant_id = #{tenantId}


        and type = #{type}


        and deposit_type = #{depositType}


        and create_time = #{createTime}


        and update_time = #{updateTime}
