mapper.select:
select tf.name as franchiseeName, tio.insurance_name as insuranceName,tio.insurance_type as
insuranceType,tio.uid, tio.user_name as userName,tio.order_id as orderId
,tui.id_number as idCard,tio.phone as phone,tio.cid as cid,tio.pay_amount as insuranceAmount,tio.create_time as
createTime,tio.valid_days as validDays,tio.status,tio.pay_type as payType
,tio.forehead,tio.update_time,tio.is_use, tio.`source_order_no`
from t_insurance_order tio left join t_franchisee tf on tio.franchisee_id=tf.id left join t_user_info tui on
tio.uid=tui.uid


        and tio.order_id = #{query.orderId}


        and tio.franchisee_id = #{query.franchiseeId}


        and tio.status = #{query.status}


        and tio.is_use = #{query.isUse}


        and tio.insurance_type = #{query.insuranceType}


        and tio.insurance_type = 0


        and tio.insurance_type in (1,2)


        and tio.user_name = #{query.userName}


        and tio.phone = #{query.phone}


        and tio.create_time >= #{query.beginTime}


        and tio.create_time <= #{query.endTime}


        and tio.status = #{query.status}


        and tio.uid = #{query.uid}


        and tio.is_use = #{query.isUse}


        and tio.tenant_id = #{query.tenantId}


        and tio.pay_type = #{query.payType}



        and tio.franchisee_id in

            #{id}



        and tio.store_id in

            #{id}



order by tio.create_time desc
limit #{query.offset},#{query.size}

select count(1) from t_insurance_order tio left join t_franchisee tf on tio.franchisee_id=tf.id


        and tio.order_id = #{query.orderId}


        and tio.franchisee_id = #{query.franchiseeId}


        and tio.is_use = #{query.isUse}


        and tio.user_name = #{query.userName}


        and tio.phone = #{query.phone}


        and tio.uid = #{query.uid}


        and tio.create_time >= #{query.beginTime}


        and tio.create_time <= #{query.endTime}


        and tio.tenant_id = #{query.tenantId}


        and tio.status = #{query.status}


        and tio.insurance_type = #{query.insuranceType}


        and tio.insurance_type = 0


        and tio.insurance_type in (1,2)


        and tio.pay_type = #{query.payType}



        and tio.franchisee_id in

            #{id}



        and tio.store_id in

            #{id}
