mapper.select:
select
iajh.id,
iajh.uid,
iajh.start_time,
iajh.expired_time,
iajh.activity_id,
iajh.status,
iajh.pay_count,
iajh.money,
iajh.create_time,
tui.name as joinUserName,
tui.phone as joinUserPhone
from t_invitation_activity_join_history iajh
left join t_user_info tui on tui.uid=iajh.join_uid


        and tui.name = #{userName}


        and tui.phone = #{phone}


        and iajh.activity_id = #{activityId}


        and iajh.record_id = #{recordId}


        and iajh.status = #{status}


        and iajh.uid = #{uid}


        and iajh.tenant_id = #{tenantId}


        and iajh.create_time >=#{beginTime}


        and iajh.create_time <=#{endTime}


order by iajh.id desc
limit #{offset}, #{size}

select count(*)
from t_invitation_activity_join_history iajh
left join t_user_info tui on tui.uid=iajh.join_uid


        and tui.name = #{userName}


        and tui.phone = #{phone}


        and iajh.activity_id = #{activityId}


        and iajh.record_id = #{recordId}


        and iajh.status = #{status}


        and iajh.tenant_id = #{tenantId}


        and iajh.create_time >=#{beginTime}


        and iajh.create_time <=#{endTime}

select
id, uid, record_id, join_uid, start_time, expired_time, activity_id, status, pay_count, money, tenant_id, create_time, update_time
from t_invitation_activity_join_history


        and id = #{id}


        and uid = #{uid}


        and record_id = #{recordId}


        and join_uid = #{joinUid}


        and start_time = #{startTime}


        and expired_time = #{expiredTime}


        and activity_id = #{activityId}


        and status = #{status}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}

mapper.update:
update t_invitation_activity_join_history
set status = #{status}
where activity_id = #{activityId} and status =1
