mapper.select:
select
iajh.id,
iajh.uid,
iajh.start_time,
iajh.expired_time,
iajh.activity_id,
tia.name as activityName,
iajh.status,
iajh.pay_count,
iajh.money,
iajh.create_time,
iajh.join_uid joinUid,
tui.name as joinUserName,
tui.phone as joinUserPhone
from t_invitation_activity_join_history iajh
left join t_user_info tui on tui.uid=iajh.join_uid
left join t_invitation_activity as tia on tia.id = iajh.activity_id


        and iajh.join_uid = #{joinUid}


        and iajh.activity_id = #{activityId}


        and iajh.record_id = #{recordId}


        and iajh.status = #{status}


        and iajh.uid = #{uid}


        and iajh.tenant_id = #{tenantId}


        and iajh.create_time >=#{beginTime}


        and iajh.create_time <=#{endTime}


        and tia.name like concat('%',#{activityName, jdbcType=VARCHAR},'%')



        and iajh.pay_count = 1



        and iajh.pay_count > 1


        and tui.franchisee_id in

            #{id}



        and tui.store_id in

            #{id}



order by iajh.id desc
limit #{offset}, #{size}

select count(*)
from t_invitation_activity_join_history iajh
left join t_user_info tui on tui.uid=iajh.join_uid
left join t_invitation_activity as tia on tia.id = iajh.activity_id


        and iajh.join_uid = #{joinUid}


        and iajh.activity_id = #{activityId}


        and iajh.record_id = #{recordId}


        and iajh.status = #{status}


        and iajh.tenant_id = #{tenantId}


        and iajh.create_time >=#{beginTime}


        and iajh.create_time <=#{endTime}


        and tia.name like concat('%',#{activityName, jdbcType=VARCHAR},'%')


        and iajh.pay_count = 1


        and iajh.pay_count > 1


        and iajh.franchisee_id = #{franchiseeId}


        and tui.franchisee_id in

            #{id}



        and tui.store_id in

            #{id}

select iajh.id, iajh.uid, iajh.start_time, iajh.expired_time, iajh.activity_id, tia.name as activityName, iajh.status, iajh.pay_count, iajh.money, iajh.create_time, iajh.join_uid joinUid, tui.name as joinUserName, tui.phone as joinUserPhone from t_invitation_activity_join_history iajh left join t_user_info tui on tui.uid=iajh.join_uid left join t_invitation_activity as tia on tia.id = iajh.activity_id
and iajh.join_uid = #{joinUid}
and iajh.activity_id = #{activityId}
and iajh.record_id = #{recordId}
and iajh.status = #{status}
and iajh.uid = #{uid}
and iajh.tenant_id = #{tenantId}
order by iajh.id desc limit #{offset}, #{size}

select
id, uid, record_id, join_uid, start_time, expired_time, activity_id, status, pay_count, money, tenant_id, create_time, update_time
from t_invitation_activity_join_history


        and id = #{id}


        and uid = #{uid}


        and record_id = #{recordId}


        and join_uid = #{joinUid}


        and start_time = #{startTime}


        and expired_time = #{expiredTime}


        and activity_id = #{activityId}


        and status = #{status}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}

mapper.update:
update t_invitation_activity_join_history
set status = #{status}
where activity_id = #{activityId} and status =1

mapper.select:

select
iajh.id,
iajh.uid,
iajh.start_time,
iajh.expired_time,
iajh.activity_id,
tia.name as activityName,
iajh.status,
iajh.pay_count,
iajh.money,
iajh.create_time,
iajh.join_uid joinUid,
iajh.franchisee_id,
tui.name as joinUserName,
tui.phone as joinUserPhone
from t_invitation_activity_join_history iajh
left join t_user_info tui on tui.uid=iajh.join_uid
left join t_user_info tu on tu.uid=iajh.uid
left join t_invitation_activity as tia on tia.id = iajh.activity_id


        and tui.name = #{userName}


        and tui.phone = #{phone}


        and iajh.join_uid = #{joinUid}


        and iajh.activity_id = #{activityId}


        and iajh.record_id = #{recordId}


        and iajh.status = #{status}


        and iajh.uid = #{uid}


        and iajh.tenant_id = #{tenantId}


        and iajh.create_time >=#{beginTime}


        and iajh.create_time <=#{endTime}


        and iajh.franchisee_id = #{franchiseeId}


        and tu.franchisee_id in

            #{id}



        and tu.store_id in

            #{id}



        and tia.name like concat('%',#{activityName, jdbcType=VARCHAR},'%')


        and iajh.pay_count = 1



        and iajh.pay_count > 1



order by iajh.id desc
limit #{offset}, #{size}

select
iajh.id,
iajh.uid,
iajh.start_time,
iajh.expired_time,
iajh.activity_id,
tia.name as activityName,
iajh.status,
iajh.pay_count,
iajh.money,
iajh.create_time,
iajh.join_uid joinUid
from t_invitation_activity_join_history iajh
left join t_invitation_activity tia on tia.id = iajh.activity_id

    iajh.uid = #{uid}

        and iajh.tenant_id = #{tenantId}


        and iajh.status = #{status}


        and iajh.create_time  >=   #{beginTime}


        and iajh.create_time  <=   #{endTime}


        and iajh.activity_id = #{activityId}


order by iajh.id desc

    limit #{offset}, #{size}

select iajh.id, iajh.uid, iajh.start_time, iajh.expired_time, iajh.activity_id, tia.name as activityName, iajh.status, iajh.pay_count, iajh.money, iajh.create_time, iajh.join_uid joinUid, iajh.package_id, iajh.package_type from t_invitation_activity_join_history iajh left join t_invitation_activity tia on tia.id = iajh.activity_id
iajh.uid = #{uid}
and iajh.tenant_id = #{tenantId}
and iajh.status = #{status}
and iajh.create_time >= #{beginTime}
and iajh.create_time <= #{endTime}
order by iajh.id desc
limit #{offset}, #{size}

select join_uid, any_value(id) as id from t_invitation_activity_join_history

    uid = #{uid}

        and tenant_id = #{tenantId}


        and status = #{status}


        and create_time  >=   #{beginTime}


        and create_time  <=   #{endTime}


group by join_uid
order by id desc

    limit #{offset}, #{size}

select id, uid, record_id, join_uid, start_time, expired_time, activity_id, status, pay_count, money
from t_invitation_activity_join_history
where join_uid = #{joinUid} and activity_id = #{activityId} and status = 1;
