mapper.select:
select
id, name, type, status, description, hours, discount_type, first_reward, other_reward, operate_uid, del_flag,
create_time, update_time,
tenant_id
from t_invitation_activity
    where del_flag = 0

    and id = #{id}


    and name = #{name}


    and type = #{type}


    and status = #{status}


    and description = #{description}


    and hours = #{hours}


    and discount_type = #{discountType}


    and operate_uid = #{operateUid}


    and del_flag = #{delFlag}


    and create_time = #{createTime}


    and update_time = #{updateTime}


    and tenant_id = #{tenantId}

select
id, name, type, status, description, hours, minutes, discount_type, first_reward, other_reward, operate_uid, del_flag,
create_time, update_time, tenant_id, franchisee_id
from t_invitation_activity
where del_flag=0

    and name = #{name}


    and status = #{status}


    and tenant_id = #{tenantId}


    and franchisee_id = #{franchiseeId}


    and franchisee_id in

        #{franchiseeId}


order by id desc
limit #{offset}, #{size}

select count(1)
from t_invitation_activity
where del_flag=0

    and name = #{name}


    and status = #{status}


    and tenant_id = #{tenantId}


    and franchisee_id = #{franchiseeId}


    and franchisee_id in

        #{franchiseeId}

select 1 from t_invitation_activity where del_flag=0 and tenant_id = #{tenantId} and status =1
and franchisee_id = #{franchiseeId}
limit 1

select id, name from t_invitation_activity where tenant_id = #{tenantId} and del_flag=0
and name like concat('%',#{name,jdbcType=VARCHAR},'%')
and status = #{status}
and franchisee_id = #{franchiseeId}
order by id

SELECT tia.id,tia.NAME
FROM t_invitation_activity tia
LEFT JOIN t_invitation_activity_user iau ON tia.id = iau.activity_id
where tia.tenant_id = #{tenantId} and tia.del_flag=0

    and tia.name like concat('%',#{name,jdbcType=VARCHAR},'%')


    and iau.uid = #{uid}


    and tia.status = #{status}

order by tia.id limit #{offset}, #{size}

SELECT tia.id,tia.NAME
FROM t_invitation_activity tia
where tia.tenant_id = #{tenantId} and tia.del_flag=0

    and tia.name like concat('%',#{name,jdbcType=VARCHAR},'%')


    and tia.status = #{status}

order by tia.id limit #{offset}, #{size}
