mapper.select:
select
id, name, type, status, description, hours, discount_type, first_reward, other_reward, operate_uid, del_flag,
create_time, update_time,
tenant_id
from t_invitation_activity


        and id = #{id}


        and name = #{name}


        and type = #{type}


        and status = #{status}


        and description = #{description}


        and hours = #{hours}


        and discount_type = #{discountType}


        and operate_uid = #{operateUid}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}

select
id, name, type, status, description, hours, discount_type, first_reward, other_reward, operate_uid, del_flag,
create_time, update_time,
tenant_id
from t_invitation_activity


        and id = #{id}


        and name = #{name}


        and type = #{type}


        and status = #{status}


        and description = #{description}


        and hours = #{hours}


        and discount_type = #{discountType}


        and operate_uid = #{operateUid}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}


order by id desc
limit #{offset}, #{size}

select count(*)
from t_invitation_activity


        and id = #{id}


        and name = #{name}


        and type = #{type}


        and status = #{status}


        and description = #{description}


        and hours = #{hours}


        and discount_type = #{discountType}


        and operate_uid = #{operateUid}



        and tenant_id = #{tenantId}
