mapper.select:
select
iar.id,
iar.activity_id,
iar.code,
iar.uid,
iar.share_count,
iar.invitation_count,
iar.status,
iar.money,
iar.create_time,
iar.update_time,
tui.name as userName,
tui.phone as phone
from t_invitation_activity_record iar
left join t_user_info tui on tui.uid=iar.uid


        and tui.name = #{userName}


        and tui.phone = #{phone}


        and tui.uid = #{uid}


        and iar.status = #{status}


        and iar.tenant_id = #{tenantId}


        and iar.create_time >=#{beginTime}


        and iar.create_time <=#{endTime}


        and tui.franchisee_id in

            #{id}



        and tui.store_id in

            #{id}



order by iar.id desc
limit #{offset}, #{size}

select count(*)
from t_invitation_activity_record iar
left join t_user_info tui on tui.uid=iar.uid


        and tui.name = #{userName}


        and tui.phone = #{phone}


        and tui.uid = #{uid}


        and iar.status = #{status}


        and iar.tenant_id = #{tenantId}


        and iar.create_time >=#{beginTime}


        and iar.create_time <=#{endTime}


        and tui.franchisee_id in

            #{id}



        and tui.store_id in

            #{id}

select
id, activity_id, code, uid, share_count, invitation_count, status, money, create_time, update_time, tenant_id
from t_invitation_activity_record


        and id = #{id}


        and activity_id = #{activityId}


        and code = #{code}


        and uid = #{uid}


        and share_count = #{shareCount}


        and invitation_count = #{invitationCount}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}
