and tui.name = #{username}
and tui.phone = #{phone}
and iar.status = #{status}
and iar.tenant_id = #{tenantId}
order by iar.id desc limit #{offset}, #{size}

select count(*)
from t_invitation_activity_record iar
left join t_user_info tui on tui.uid=iar.uid


        and tui.name = #{username}


        and tui.phone = #{phone}


        and iar.status = #{status}


        and iar.tenant_id = #{tenantId}

select
id, activity_id, code, uid, share_count, invitation_count, status, create_time, update_time, tenant_id
from t_invitation_activity_record


        and id = #{id}


