mapper.select:
select  id, activity_id, uid
from t_invitation_activity_user
where uid = #{uid}

select
  iau.id id, iau.activity_id , iau.uid uid, iau.operator operator, iau.create_time create_time, iau.update_time update_time,
  tui.name as userName ,tui.phone as phone
from t_invitation_activity_user iau left join t_user_info tui on tui.uid=iau.uid


        and tui.uid = #{uid}


        and tui.name = #{userName}


        and tui.phone = #{phone}


        and tui.tenant_id = #{tenantId}


order by iau.id desc
limit #{offset}, #{size}

select count(1)
from t_invitation_activity_user iau left join t_user_info tui on tui.uid=iau.uid


        and tui.uid = #{uid}


        and tui.name = #{userName}


        and tui.phone = #{phone}


        and tui.tenant_id = #{tenantId}
