mapper.select:
select
  id, uid, join_uid, start_time, expired_time, status, del_flg, create_time, update_time, tenant_id
from t_join_share_activity_record


        and id = #{id}


        and uid = #{uid}


        and join_uid = #{joinUid}


        and start_time = #{startTime}


        and expired_time = #{expiredTime}


        and status = #{status}


        and del_flg = #{delFlg}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}
