select
jsah.uid as uid, jsah.start_time as startTime,
jsah.expired_time as expiredTime,jsah.status as status,
tui.name as join_name, tui.phone as join_phone
from t_join_share_money_activity_history as jsah
left join t_user_info as tui on tui.uid = jsah.join_uid


        and jsah.uid = #{uid}


        and jsah.activity_id = #{activityId}


        and jsah.tenant_id = #{tenantId}


        and jsah.status = #{status}


        and tui.name like concat('%', #{joinName}, '%')


        and jsah.start_time >= #{beginTime}


        and jsah.start_time <= #{endTime}


order by jsah.update_time desc
limit #{offset}, #{size}

select tjsmah.id as id, tjsmah.activity_id as activityId, tsma.invitation_criteria as invitationCriteria, tsma.name as activityName, tjsmah.uid as inviterUid, tjsmah.join_uid as joinUid, tui.name as joinName, tui.phone as joinPhone, tjsmah.start_time as startTime, tjsmah.expired_time as expiredTime, tjsmah.status as status, tjsmah.create_time as createTime, tjsmah.update_time as updateTime, tjsmah.tenant_id as tenantId
from t_join_share_money_activity_history tjsmah left join t_user_info as tui on tui.uid = tjsmah.join_uid left join t_share_money_activity as tsma on tsma.id = tjsmah.activity_id
and tjsmah.uid = #{uid}
and tjsmah.activity_id = #{activityId}
and tjsmah.tenant_id = #{tenantId}
and tjsmah.status = #{status}
and tui.name like concat('%', #{joinName}, '%')
and tui.phone = #{phone}
and tsma.name like concat('%', #{activityName}, '%')
and tjsmah.start_time >= #{beginTime}
and tjsmah.start_time <= #{endTime}
order by tjsmah.update_time desc
limit #{offset}, #{size}

