mapper.select:
select id,
status,
remark,
type,
create_time,
update_time,
pic,
uid,
phone,
operate_uid,
operate_remark,
electricity_cabinet_id,
tenant_id
from t_maintenance_record


		and status = #{status}


		and uid = #{uid}


		and tenant_id = #{tenantId}


		and phone = #{phone}


		and type = #{type}


		and electricity_cabinet_id = #{electricityCabinetId}


		and create_time >=#{beginTime}


		and create_time <=#{endTime}


		and electricity_cabinet_id in

			#{id}



order by create_time desc
limit #{offset}, #{size}

select count(1)
from t_maintenance_record


		and status = #{status}


		and uid = #{uid}


		and tenant_id = #{tenantId}


		and phone = #{phone}


		and type = #{type}


		and electricity_cabinet_id = #{electricityCabinetId}


		and create_time >=#{beginTime}


		and create_time <=#{endTime}


		and electricity_cabinet_id in

			#{id}
