mapper.select:
select
id, status, remark, type, create_time, update_time, pic, uid, phone, operate_uid
electricity_cabinet_id,operate_remark
from t_maintenance_record


		and id = #{id}


		and status = #{status}


		and remark = #{remark}


		and type = #{type}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and pic = #{pic}


		and uid = #{uid}


		and phone = #{phone}


		and operate_uid = #{operateUid}


		and operate_remark = #{operateRemark}

select id,
status,
remark,
type,
create_time,
update_time,
pic,
uid,
phone,
operate_uid,
operate_remark,
electricity_cabinet_id
from t_maintenance_record


		and status = #{status}


		and uid = #{uid}


		and phone = #{phone}


		and type = #{type}


		and electricity_cabinet_id = #{electricityCabinetId}


		and create_time >=#{beginTime}


		and create_time <=#{endTime}


order by create_time desc
limit #{offset}, #{size}

select count(1)
from t_maintenance_record


		and status = #{status}


		and uid = #{uid}


		and phone = #{phone}


		and type = #{type}


		and electricity_cabinet_id = #{electricityCabinetId}


		and create_time >=#{beginTime}


		and create_time <=#{endTime}
