mapper.select:
select
id, name, type, status, time_type,begin_time,end_time,discount_type, count,days,coupon_id,
description, del_flg, create_time,update_time, uid,user_name,franchisee_id
from t_new_user_activity


		and name = #{query.name}


		and tenant_id = #{query.tenantId}


		and status = #{query.status}


order by update_time desc

	limit #{query.offset}, #{query.size}

select count(1)
from t_new_user_activity


		and name = #{query.name}


		and tenant_id = #{query.tenantId}


		and status = #{query.status}
