mapper.select:
select
id, name, type, status, time_type,begin_time,end_time,discount_type, count,coupon_id,user_scope,
description, del_flg, create_time,update_time, uid,user_name,franchisee_id
from t_old_user_activity


        and name = #{query.name}


        and user_scope = #{query.userScope}


        and status = #{query.status}


        and discount_type = #{query.discountType}


        and tenant_id = #{query.tenantId}


order by update_time desc

    limit #{query.offset}, #{query.size}

select count(1)
from t_old_user_activity


        and name = #{query.name}


        and tenant_id = #{query.tenantId}


        and user_scope = #{query.userScope}


        and status = #{query.status}
