mapper.select:
select
id, order_id,uid,name , phone, electricity_battery_sn, battery_deposit,electricity_cabinet_id,cell_no,type,
status, create_time,update_time,remark
from t_rent_battery_order


        and order_id like concat('%',#{query.orderId,jdbcType=VARCHAR},'%')


        and name = #{query.name}


        and phone = #{query.phone}


        and uid = #{query.uid}


        and status = #{query.status}


        and type = #{query.type}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


        and tenant_id = #{query.tenantId}


        and franchisee_id in

            #{id}



        and store_id in

            #{id}



order by create_time desc
limit #{query.offset}, #{query.size}

select  COUNT(1)
from t_rent_battery_order


        and order_id like concat('%',#{query.orderId,jdbcType=VARCHAR},'%')


        and name = #{query.name}


        and phone = #{query.phone}


        and uid = #{query.uid}


        and status = #{query.status}


        and type = #{query.type}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


        and tenant_id = #{query.tenantId}


        and franchisee_id in

            #{id}



        and store_id in

            #{id}

select temp.uid, temp.type, temp.order_id, temp.old_electricity_battery_sn, temp.old_cell_no, temp.new_electricity_battery_sn, temp.new_cell_no, temp.electricity_cabinet_id, temp.create_time from ( select uid, type, order_id, case when type = 2 then electricity_battery_sn else null end as old_electricity_battery_sn, case when type = 2 then cell_no else null end as old_cell_no, case when type = 1 then electricity_battery_sn else null end as new_electricity_battery_sn, case when type = 1 then cell_no else null end as new_cell_no, electricity_cabinet_id, create_time from t_rent_battery_order where electricity_cabinet_id = #{query.id} and type in (1,2) union all select uid, 9 type, order_id, old_electricity_battery_sn , old_cell_no , new_electricity_battery_sn, new_cell_no, electricity_cabinet_id, create_time from t_electricity_cabinet_order where electricity_cabinet_id = #{query.id}) temp
and temp.uid = #{query.uid}
and temp.create_time >= #{query.beginTime}
and temp.create_time <= #{query.endTime}
order by temp.create_time desc limit #{query.offset}, #{query.size}

select count(1) from (select uid, type, order_id, case when type = 2 then electricity_battery_sn else null end as old_electricity_battery_sn, case when type = 2 then cell_no else null end as old_cell_no, case when type = 1 then electricity_battery_sn else null end as new_electricity_battery_sn, case when type = 1 then cell_no else null end as new_cell_no, create_time from t_rent_battery_order where electricity_cabinet_id = #{query.id} and type in (1,2) union all select uid, 9 type, teco.order_id, teco.old_electricity_battery_sn, teco.old_cell_no, teco.new_electricity_battery_sn, teco.new_cell_no, teco.create_time from t_electricity_cabinet_order teco where electricity_cabinet_id = #{query.id}) temp
and temp.uid = #{query.uid}
and temp.create_time >= #{query.beginTime}
and temp.create_time <= #{query.endTime}
