mapper.select:
select
id, order_id,uid,user_name , phone, electricity_battery_sn, battery_deposit,electricity_cabinet_id,cell_no,type
status, create_time,update_time
from electricity.t_rent_battery_order


        and name = #{query.name}


        and phone = #{query.phone}


        and status = #{query.status}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


        and status = #{query.status}


order by create_time desc
