        and name = #{name}


        and phone = #{phone}


        and id_number = #{idNumber}


        and battery_store_id = #{batteryStoreId}


        and battery_area_id = #{batteryAreaId}


        and electricity_battery_sn = #{electricityBatterySn}


        and battery_deposit = #{batteryDeposit}


        and status = #{status}


        and create_time = #{createTime}
