mapper.select:
select
id, order_id,uid,name , phone, electricity_battery_sn, battery_deposit,electricity_cabinet_id,cell_no,type,
status, create_time,update_time
from t_rent_battery_order


        and order_id = #{query.orderId}


        and name = #{query.name}


        and phone = #{query.phone}


        and status = #{query.status}


        and type = #{query.type}


        and create_time >=#{query.beginTime}


        and create_time <=#{query.endTime}


        and electricity_cabinet_id in

            #{id}



order by create_time desc
limit #{query.offset}, #{query.size}
