mapper.select:
select
  id, uid, name, phone, id_number, car_store_id, car_store_name, car_sn, number_plate, car_deposit, status, create_time
from electricity.t_rent_car_order


        and id = #{id}


        and uid = #{uid}


        and name = #{name}


        and phone = #{phone}


        and id_number = #{idNumber}


        and car_store_id = #{carStoreId}


        and car_store_name = #{carStoreName}


        and car_sn = #{carSn}


        and number_plate = #{numberPlate}


        and car_deposit = #{carDeposit}


        and status = #{status}


        and create_time = #{createTime}
