mapper.update:
update t_service_phone
set phone       = #{phone},
    remark      = #{remark},
    update_time = #{updateTime}
where id = #{id}
  and tenant_id = #{tenantId}

update t_service_phone
set phone       = #{newPhone},
    update_time = #{updateTime}
where phone = #{oldPhone}
  and tenant_id = #{tenantId}
  and del_flag = 0

mapper.select:
select

from t_service_phone
where phone = #{newPhone}
and tenant_id = #{tenantId}
and del_flag = 0
