mapper.select:
select
id, name, type, status,  description, invitation_criteria, del_flag, create_time,receive_type,
update_time, uid,user_name,franchisee_id,hours,minutes,invitation_criteria
from t_share_activity
where del_flag = 0

    and name = #{query.name}


    and type in

        #{id}



    and franchisee_id in

        #{id}



    and tenant_id = #{query.tenantId}


    and status = #{query.status}


    and invitation_criteria = #{query.invitationCriteria}


    and franchisee_id = #{query.franchiseeId}

order by update_time desc

    limit #{query.offset}, #{query.size}

select count(1)
from t_share_activity
where del_flag = 0

    and name = #{query.name}


    and type in

        #{id}



    and franchisee_id in

        #{id}



    and tenant_id = #{query.tenantId}


    and status = #{query.status}


    and invitation_criteria = #{query.invitationCriteria}


    and franchisee_id = #{query.franchiseeId}

select id, name, type, status, description, invitation_criteria, del_flag, create_time,receive_type, update_time, uid,user_name,franchisee_id,hours,minutes,invitation_criteria,franchisee_id from t_share_activity
tenant_id = #{tenantId} and status = 1
and franchisee_id = #{franchiseeId}
