mapper.select:
select
activity_id, member_card_id, package_type
from t_share_activity_member_card
where activity_id = #{activityId}
and package_type = #{packageType}

select   member_card_id
from t_share_activity_member_card
where activity_id = #{activityId}

select
id, activity_id, member_card_id, create_time, update_time, tenant_id
from t_share_activity_member_card


        and id = #{id}


        and activity_id = #{activityId}


        and member_card_id = #{memberCardId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}
