mapper.select:
select id, share_activity_id, uid, name, member_card, create_time, update_time from t_share_activity_operate_record
and uid = #{uid}
and name like concat('%',#{name,jdbcType=VARCHAR},'%')
and tenant_id = #{tenantId}
order by create_time desc limit #{offset}, #{size}

select
count(1)
from t_share_activity_operate_record


        and uid = #{uid}


        and name like concat('%',#{name,jdbcType=VARCHAR},'%')



        and tenant_id = #{tenantId}

select
id, share_activity_id, uid, NAME, member_card, tenant_id, create_time, update_time
from t_share_activity_operate_record


        and id = #{id}


        and share_activity_id = #{shareActivityId}


        and uid = #{uid}


        and NAME = #{name}


        and member_card = #{memberCard}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}
