mapper.select:
select
  id, activity_id, code, uid, count, del_flg, create_time, update_time, tenant_id
from t_share_activity_record


        and id = #{id}


        and activity_id = #{activityId}


        and code = #{code}


        and uid = #{uid}


        and count = #{count}


        and del_flg = #{delFlg}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}
