mapper.select:
select
  id, activity_id, coupon_id, discount_type, coupon_count, receive_count, del_flg, create_time, update_time, status
from electricity.t_activity_bind_coupon


        and id = #{id}


        and activity_id = #{activityId}


        and coupon_id = #{couponId}


        and discount_type = #{discountType}


        and coupon_count = #{couponCount}


        and receive_count = #{receiveCount}


        and del_flg = #{delFlg}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and status = #{status}
