mapper.select:
select
id, name, type, status,  description, del_flg, create_time,
update_time, uid,user_name,franchisee_id,hours,money
from t_share_money_activity


        and name = #{query.name}


        and tenant_id = #{query.tenantId}


order by update_time desc

    limit #{query.offset}, #{query.size}

select count(1)
from t_share_money_activity


        and name = #{query.name}


        and tenant_id = #{query.tenantId}
